import React, { useEffect, useRef } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

const imageBasePath = "/static/frontend/images/";

export default function Products() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);

  useEffect(() => {
    const scrollRow = (ref) => {
      if (!ref.current) return;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;

      const slide = setInterval(() => {
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;

        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);

      return () => clearInterval(slide);
    };

    const stop1 = scrollRow(row1Ref);
    const stop2 = scrollRow(row2Ref);

    return () => {
      stop1 && stop1();
      stop2 && stop2();
    };
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <div className="w-full bg-gray-900 text-white flex items-center justify-between px-40 py-4 fixed top-0 z-50 shadow-md">
        <div className="text-sm font-bold uppercase">
          <a href="/" className="hover:underline text-orange-400">Home</a>
        </div>
        <div className="flex-1 flex justify-center space-x-10 font-bold text-base uppercase">
          <a href="/products" className="hover:text-orange-400">Our Products</a>
          <a href="/#contact" className="hover:underline">Contact Us</a>
          <a href="/quote" className="hover:underline text-orange-400">Request a Quote</a>
        </div>
        <div className="w-1/4 flex justify-end">
          <img
            src={`${imageBasePath}jendyco-logo2.png`}
            alt="Jendyco Logo"
            className="w-40 h-auto drop-shadow-[0_0_8px_white]"
          />
        </div>
      </div>

      <div className="pt-52 px-8 text-center">
        <h1 className="text-4xl font-bold text-[#002b5c] mb-12 tracking-wide">Our Products</h1>
      </div>

      {/* Product Highlights */}
      <div className="px-8 py-12 grid md:grid-cols-2 lg:grid-cols-3 gap-10 text-center">
        {[
          {
            title: "Peruvian Coffee",
            description: "Handpicked at high altitudes, our Arabica beans (87%+ purity) are smooth, bold, and certified export-grade.",
            image: "Jendycoffee-branch.JPG"
          },
          {
            title: "Cacao Beans",
            description: "Rich cacao with high butter content and deep flavor, ethically grown in Peru’s fertile valleys.",
            image: "JendycoCacao-Branch2.JPG"
          },
          {
            title: "Pima Cotton",
            description: "Famous for its unmatched softness, strength, and sheen — Peru’s premium textile for luxury fashion.",
            image: "JendycoFields1.jpg"
          },
          {
            title: "Camu Camu Powder",
            description: "One of the world’s richest natural sources of Vitamin C — wild harvested and sun-dried in the Amazon.",
            image: "camu-camu.jpg"
          },
          {
            title: "Lucuma Powder",
            description: "Known as the 'Gold of the Incas,' this naturally sweet fruit powder is perfect for healthy recipes.",
            image: "lucuma.jpg"
          }
        ].map((item, idx) => (
          <div key={idx} className="bg-white rounded-xl shadow-lg p-6 hover:shadow-xl transition">
            <img
              src={`${imageBasePath}${item.image}`}
              alt={item.title}
              className="w-full h-48 object-cover rounded mb-4"
            />
            <h2 className="text-xl font-bold text-[#002b5c] mb-2">{item.title}</h2>
            <p className="text-sm text-gray-700 leading-relaxed">{item.description}</p>
          </div>
        ))}
      </div>

      {/* Slideshow Section */}
      <div className="bg-white px-8 py-20 overflow-hidden">
        <h2 className="text-2xl font-bold text-center mb-12 text-[#002b5c]">
          Our Coffee & Cacao in Action — Jendy in Motion
        </h2>

        <div className="overflow-hidden" ref={row1Ref}>
          <div className="flex gap-6 w-max">
            {["JendycoFields1.jpg", "Jendycoffee-Branch2.JPG", "Jendycoffee-branch.JPG", "Jendycocoffeebeans-closeup.JPG"].map((filename, index) => (
              <img
                key={`row1-${index}`}
                src={`${imageBasePath}${filename}`}
                alt={`Jendyco ${index + 1}`}
                className="w-[300px] h-[200px] object-cover rounded shadow-md"
              />
            ))}
          </div>
        </div>

        <div className="overflow-hidden mt-10" ref={row2Ref}>
          <div className="flex gap-6 w-max">
            {["JendycoCoffee-Branch3.JPG", "JendycoCacao-Branch3.JPG", "JendycoCacao-Branch2.JPG", "JendycoCacao-Branch.JPG"].map((filename, index) => (
              <img
                key={`row2-${index}`}
                src={`${imageBasePath}${filename}`}
                alt={`Jendyco ${index + 5}`}
                className="w-[300px] h-[200px] object-cover rounded shadow-md"
              />
            ))}
          </div>
        </div>
      </div>

      {/* Baby Alpaca Section */}
      <section className="bg-[#fff8f0] px-10 py-20 text-gray-800 text-center">
        <h2 className="text-3xl font-bold text-[#002b5c] mb-6">100% Baby Alpaca Wool</h2>
        <p className="max-w-3xl mx-auto text-gray-700 text-lg leading-relaxed">
          Luxurious, hypoallergenic, and sustainably sourced. We offer all three quality tiers:
          Royal (purest), Baby, and Superfine. Perfect for premium fashion and global textile buyers.
        </p>
      </section>

      {/* Vicuña Wool On-Demand Section */}
      <section className="bg-[#e6f2ff] px-10 py-20 text-gray-800 text-center">
        <h2 className="text-3xl font-bold text-[#002b5c] mb-6">Vicuña Wool (On-Demand)</h2>
        <p className="max-w-3xl mx-auto text-gray-700 text-lg leading-relaxed">
          The rarest and most expensive wool in the world. We don’t stock it — but with your request, we’ll source it ethically from trusted Peruvian partners.
        </p>
        <a href="/quote" className="mt-6 inline-block bg-orange-500 hover:bg-orange-600 text-white px-6 py-3 rounded-full text-sm font-bold transition">Request Availability</a>
      </section>

      {/* Custom Sourcing CTA */}
      <section className="bg-gray-100 px-10 py-20 text-gray-800 text-center">
        <h2 className="text-3xl font-bold text-[#002b5c] mb-6">Looking for Something Else?</h2>
        <p className="max-w-3xl mx-auto text-gray-700 text-lg leading-relaxed">
          We specialize in custom sourcing across all regions of Peru. Whether it’s rare fruits, herbs, minerals, or textiles — we’ll find it for you.
        </p>
        <a href="/quote" className="mt-6 inline-block bg-[#002b5c] hover:bg-blue-900 text-white px-6 py-3 rounded-full text-sm font-bold transition">Send a Request</a>
      </section>

      <footer className="bg-gray-200 text-center py-8 text-sm text-gray-600">
        <div className="mb-4 flex justify-center gap-6">
          <a
            href="https://www.instagram.com/jendyco_international"
            target="_blank"
            rel="noopener noreferrer"
            aria-label="Instagram"
          >
            <FaInstagram className="text-[#E1306C] text-2xl hover:scale-110 transition" />
          </a>
          <a
            href="https://www.linkedin.com/in/jendyco-international-27647b36b"
            target="_blank"
            rel="noopener noreferrer"
            aria-label="LinkedIn"
          >
            <FaLinkedin className="text-[#0077B5] text-2xl hover:scale-110 transition" />
          </a>
        </div>
        © {new Date().getFullYear()} Jendyco International. All rights reserved.
      </footer>
    </div>
  );
}
