// Home.js (Unified Jendyco Page: Home + Products + Quote)
import React, { useEffect, useRef, useState } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

export default function Home() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);
  const productsRef = useRef(null);
  const quoteRef = useRef(null);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    company: "",
    products: [],
    details: "",
  });

  const handleChange = (e) => {
    const { name, value, checked } = e.target;
    if (name === "products") {
      const updatedProducts = [...formData.products];
      if (checked) updatedProducts.push(value);
      else updatedProducts.splice(updatedProducts.indexOf(value), 1);
      setFormData({ ...formData, products: updatedProducts });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/api/send-quote/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      if (data.success) {
        alert("✅ Your quote request has been sent successfully!");
        setFormData({ name: "", email: "", company: "", products: [], details: "" });
      } else {
        alert("❌ There was an error: " + data.error);
      }
    } catch (error) {
      alert("❌ Network error: " + error.message);
    }
  };

  const productOptions = [
    "Arabica Coffee Beans", "Cacao Beans", "Pima Cotton", "Camu Camu Powder",
    "Lucuma Powder", "Baby Alpaca Wool - Royal", "Baby Alpaca Wool - Baby",
    "Baby Alpaca Wool - Regular", "Vicuña Wool", "Mixed Shipment", "Other / Custom"
  ];

  const scrollToSection = (ref) => {
    if (ref?.current) ref.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    function startScrolling(ref) {
      if (!ref.current) return null;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;
      const interval = setInterval(() => {
        if (!container) return;
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;
        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);
      return interval;
    }
    const interval1 = startScrolling(row1Ref);
    const interval2 = startScrolling(row2Ref);
    return () => {
      clearInterval(interval1);
      clearInterval(interval2);
    };
  }, []);

  return (
    <div className="flex flex-col min-h-screen font-sans text-gray-900">
      {/* NAVBAR */}
      <div className="fixed top-0 w-full bg-gray-900 text-white z-50 shadow-md py-4 px-10 md:px-40 flex justify-between items-center">
        <img src="/images/jendyco-logo2.png" alt="Logo" className="w-36 md:w-52 h-auto drop-shadow-[0_0_8px_white]" />
        <div className="flex space-x-6 md:space-x-10 font-bold text-sm md:text-base uppercase">
          <a href="#who-we-are" className="hover:text-orange-400">Who We Are</a>
          <button onClick={() => scrollToSection(productsRef)} className="hover:text-orange-400">Our Products</button>
          <a href="#contact" className="hover:text-orange-400">Contact Us</a>
          <button onClick={() => scrollToSection(quoteRef)} className="hover:text-orange-400">Request a Quote</button>
        </div>
        <div className="w-1/4" />
      </div>

      {/* rest of content stays the same */}

      {/* PRODUCTS */}
      <div ref={productsRef} className="pt-40 px-8 text-center bg-gray-100">
        <h1 className="text-4xl font-bold text-[#002b5c] mb-12 tracking-wide">Our Products</h1>
        {/* rest of product grid */}
      </div>

      {/* rest of content stays the same */}
    </div>
  );
}
