// Home.js (Unified Jendyco Page: Home + Products + Quote)
import React, { useEffect, useRef, useState } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

export default function Home() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);
  const productsRef = useRef(null);
  const quoteRef = useRef(null);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    company: "",
    products: [],
    details: "",
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (name === "products") {
      const updatedProducts = [...formData.products];
      if (checked) updatedProducts.push(value);
      else updatedProducts.splice(updatedProducts.indexOf(value), 1);
      setFormData({ ...formData, products: updatedProducts });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/api/send-quote/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      if (data.success) {
        alert("✅ Your quote request has been sent successfully!");
        setFormData({ name: "", email: "", company: "", products: [], details: "" });
      } else {
        alert("❌ There was an error: " + data.error);
      }
    } catch (error) {
      alert("❌ Network error: " + error.message);
    }
  };

  const productOptions = [
    "Arabica Coffee Beans", "Cacao Beans", "Pima Cotton", "Camu Camu Powder",
    "Lucuma Powder", "Baby Alpaca Wool - Royal", "Baby Alpaca Wool - Baby",
    "Baby Alpaca Wool - Regular", "Vicuña Wool", "Mixed Shipment", "Other / Custom"
  ];

  const scrollToSection = (ref) => {
    if (ref?.current) ref.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    const interval1 = startScrolling(row1Ref);
    const interval2 = startScrolling(row2Ref);

    function startScrolling(ref) {
      if (!ref.current) return null;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;

      return setInterval(() => {
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;
        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);
    }

    return () => {
      clearInterval(interval1);
      clearInterval(interval2);
    };
  }, []);


  return (
    <div className="flex flex-col min-h-screen font-sans text-gray-900">
      {/* NAVBAR */}
      <div className="fixed top-0 w-full bg-gray-900 text-white z-50 shadow-md py-4 px-10 md:px-40 flex justify-between items-center">
        <img src="/images/jendyco-logo2.png" alt="Logo" className="w-36 md:w-52 h-auto drop-shadow-[0_0_8px_white]" />
        <div className="flex space-x-6 md:space-x-10 font-bold text-sm md:text-base uppercase">
          <a href="/who-we-are" className="hover:text-orange-400">Who We Are</a>
          <button onClick={() => scrollToSection(productsRef)} className="hover:text-orange-400">Our Products</button>
          <a href="/contact" className="hover:text-orange-400">Contact Us</a>
          <button onClick={() => scrollToSection(quoteRef)} className="hover:text-orange-400">Request a Quote</button>
        </div>
        <div className="w-1/4" />
      </div>

      {/* HERO SECTION */}
      <div
        className="h-[110vh] bg-cover bg-center bg-no-repeat relative flex items-center justify-center px-8 pt-28"
        style={{ backgroundImage: "url('/images/background_main2.png')" }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-50 z-0" />
        <div className="text-center text-white px-6 max-w-4xl z-10">
          <p className="font-[Playfair] text-4xl md:text-5xl leading-snug font-semibold drop-shadow-md tracking-wide">
            Global Trade. Local Roots.
          </p>
          <p className="mt-4 text-lg md:text-xl font-light leading-relaxed drop-shadow-sm">
            We connect the world to premium Peruvian exports — coffee, cacao, cotton, and more. Built on trust, driven by integrity.
          </p>
          <p className="mt-6 text-base italic text-orange-200">“From Peru to the world — with purpose and precision.”</p>
          <div className="mt-8 flex flex-wrap justify-center gap-6">
            <button onClick={() => scrollToSection(productsRef)} className="bg-orange-500 hover:bg-orange-600 text-white text-lg font-bold px-6 py-3 rounded-lg shadow-lg transition transform hover:scale-105">
              Explore Our Products
            </button>
            <button onClick={() => scrollToSection(quoteRef)} className="bg-white text-orange-500 font-bold text-lg px-6 py-3 rounded-lg shadow-lg hover:bg-gray-200 transition transform hover:scale-105">
              Request a Quote
            </button>
          </div>
          <div className="mt-14 animate-bounce">
            <button onClick={() => scrollToSection(productsRef)} className="text-white text-6xl hover:text-orange-400 transition">
              ↓
            </button>
          </div>
        </div>
      </div>

      {/* WHAT WE OFFER */}
      <section id="What-We-Offer" className="bg-white px-10 py-20 text-center pt-32 -mt-32">
        <h2 className="text-4xl font-bold mb-12 text-[#001f3f]">What We Offer</h2>
        <div className="grid md:grid-cols-3 gap-8">
          <div className="bg-white border-2 border-orange-500 rounded-xl shadow-md p-6">
            <h3 className="text-xl font-semibold mb-2 text-orange-600">Peruvian Sourcing</h3>
            <p>Arabica coffee, cacao, cotton, camu camu — ethically and expertly sourced.</p>
          </div>
          <div className="bg-white border-2 border-orange-500 rounded-xl shadow-md p-6">
            <h3 className="text-xl font-semibold mb-2 text-orange-600">Trade & Logistics</h3>
            <p>Full-spectrum support: documentation, customs, shipping, and more.</p>
          </div>
          <div className="bg-white border-2 border-orange-500 rounded-xl shadow-md p-6">
            <h3 className="text-xl font-semibold mb-2 text-orange-600">Custom Sourcing</h3>
            <p>Need baby alpaca, vicuña wool, or rare goods? We’ll make it happen.</p>
          </div>
        </div>
      </section>

      {/* WHY PERU */}
      <section className="bg-orange-50 px-10 py-20 text-center">
        <h2 className="text-4xl font-bold mb-6 text-orange-500">Why Peru?</h2>
        <p className="text-lg max-w-4xl mx-auto">
          Peru is rich in biodiversity, altitude, climate, and heritage. That translates into unmatched sourcing opportunities with a focus on sustainability and quality.
        </p>
      </section>

      {/* WHO WE ARE */}
      <section id="who-we-are" className="bg-white px-10 py-20 text-center">
        <h2 className="text-4xl font-bold mb-6 text-orange-500">Who We Are</h2>
        <p className="text-lg max-w-3xl mx-auto">
          Jendyco International is a family-driven trade hub founded on the American Dream. We believe in transparency, fair trade, and long-term relationships built on trust.
        </p>
      </section>

      {/* PRODUCTS */}
      <div ref={productsRef} className="mt-16" />

      <div ref={productsRef} className="pt-52 px-8 text-center bg-gray-100">
  <h1 className="text-4xl font-bold text-[#002b5c] mb-12 tracking-wide">Our Products</h1>

  {/* Product Grid */}
  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-12 px-10 mb-20">
    {["Peruvian Arabica Coffee", "Cacao Beans", "Pima Cotton", "Camu Camu & Lucuma Powders"].map((title, index) => {
      const descriptions = [
        "Sourced from high-altitude farms in Peru, our 87% purity Arabica coffee is handpicked and sun-dried for peak flavor. Each batch reflects generations of Peruvian expertise and a deep respect for the land.",
        "Rich in cocoa butter and low in acidity, our cacao beans come from fertile valleys in Peru. Ideal for premium chocolate production, our supply ensures traceability, quality, and ethical practices.",
        "Known for its long fibers and unparalleled softness, Peruvian Pima cotton is a luxury textile used by the world’s top fashion houses. Grown in coastal valleys and harvested with care.",
        "Camu Camu is one of the richest sources of vitamin C on Earth. Lucuma, known as the 'Gold of the Incas', brings natural sweetness to smoothies and desserts. Both grown deep in the Amazon and Andes."
      ];
      return (
        <div
          key={index}
          className="bg-white border-2 border-orange-400 rounded-xl p-6 shadow-lg transition transform duration-300"
        >
          <h2 className="text-xl font-bold text-[#002b5c] mb-2">{title}</h2>
          <p className="text-sm text-[#002b5c] leading-relaxed mb-4">{descriptions[index]}</p>
          <a
            href="#quote"
            className="inline-block mt-2 text-sm text-orange-500 hover:underline font-semibold"
          >
            Request This Product →
          </a>
          <p className="text-xs text-gray-500 italic mt-1">Minimum Order: 500kg • FOB Available</p>
        </div>
      );
    })}
  </div>

  {/* Baby Alpaca Section */}
  <div className="bg-[#fff8f0] px-12 py-32 text-center">
    <h2 className="text-5xl font-extrabold text-[#002b5c] mb-6 tracking-wide">Baby Alpaca Wool</h2>
    <p className="text-lg max-w-5xl mx-auto text-[#333] leading-loose font-medium">
      Discover the legacy of softness with Baby Alpaca wool — warmer than sheep wool, softer than cashmere, and completely hypoallergenic.
      <br /><br />
      <span className="text-orange-500 font-semibold">Royal Alpaca</span>: The top 1% of alpaca fleece. Ultra-fine and ideal for elite fashion.
      <br />
      <span className="text-orange-500 font-semibold">Baby Alpaca</span>: Premium texture and warmth, used in luxury apparel globally.
      <br />
      <span className="text-orange-500 font-semibold">Regular Alpaca</span>: Versatile, strong, and still more luxurious than most wools.
      <br /><br />
      Each thread supports Peru’s heritage, ethically sourced from trusted Andean herders.
    </p>
    <a
      href="#quote"
      className="inline-block mt-6 text-sm text-orange-500 hover:underline font-semibold"
    >
      Request Baby Alpaca Wool →
    </a>
  </div>

  {/* Vicuña Section */}
  <div className="bg-[#f0f7ff] px-12 py-32 text-center">
    <h2 className="text-5xl font-extrabold text-[#002b5c] mb-6 tracking-wide">Vicuña Wool</h2>
    <p className="text-lg max-w-5xl mx-auto text-[#333] leading-loose font-medium">
      Once reserved for Incan royalty, Vicuña wool is the rarest, finest fiber on Earth. It is lightweight, breathable, and warmer than any other natural fiber.
      <br /><br />
      Sheared only every three years under strict conservation rules, this wool is extremely limited in supply.
      <br />
      While not in stock, Jendyco offers qualified sourcing for exclusive Vicuña wool upon request.
      <br /><br />
      <span className="text-orange-500 font-semibold">Luxury beyond compare — sourced ethically, delivered globally.</span>
    </p>
    <a
      href="#quote"
      className="inline-block mt-6 text-sm text-orange-500 hover:underline font-semibold"
    >
      Request Vicuña Wool →
    </a>
  </div>

  {/* Slideshow */}
  <div className="bg-gray-100 px-8 py-20 overflow-hidden">
    <h2 className="text-2xl font-bold text-center mb-12 text-[#002b5c]">
      Our Coffee & Cacao in Action — Jendy in Motion
    </h2>
    <div className="overflow-hidden" ref={row1Ref}>
      <div className="flex gap-6 w-max">
        {["JendycoFields1.jpg", "Jendycoffee-Branch2.JPG", "Jendycoffee-branch.JPG", "Jendycocoffeebeans-closeup.JPG"].map((filename, index) => (
          <img
            key={index}
            src={`/images/${filename}`}
            alt={`Slide ${index + 1}`}
            className="w-[300px] h-[200px] object-cover rounded shadow-md"
          />
        ))}
      </div>
    </div>
    <div className="overflow-hidden mt-10" ref={row2Ref}>
      <div className="flex gap-6 w-max">
        {["JendycoCoffee-Branch3.JPG", "JendycoCacao-Branch3.JPG", "JendycoCacao-Branch2.JPG", "JendycoCacao-Branch.JPG"].map((filename, index) => (
          <img
            key={index}
            src={`/images/${filename}`}
            alt={`Slide ${index + 5}`}
            className="w-[300px] h-[200px] object-cover rounded shadow-md"
          />
        ))}
      </div>
    </div>
  </div>
</div>

<div className="pt-52 px-6 md:px-40 py-16 bg-white" ref={quoteRef}>
        <h1 className="text-4xl font-bold text-center text-[#002b5c] mb-10">Request a Quote</h1>
        <form onSubmit={handleSubmit} className="max-w-3xl mx-auto bg-gray-50 p-10 rounded-xl shadow-lg space-y-6">
          <div>
            <label htmlFor="name" className="block font-semibold mb-1 text-sm text-gray-700">Full Name</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} required className="w-full border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-400" />
          </div>
          <div>
            <label htmlFor="email" className="block font-semibold mb-1 text-sm text-gray-700">Email</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required className="w-full border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-400" />
          </div>
          <div>
            <label htmlFor="company" className="block font-semibold mb-1 text-sm text-gray-700">Company Name</label>
            <input type="text" name="company" value={formData.company} onChange={handleChange} className="w-full border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-400" />
          </div>
          <div>
            <label className="block font-semibold mb-2 text-sm text-gray-700">Products Interested In</label>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {productOptions.map((item, idx) => (
                <label key={idx} className="flex items-center space-x-2 text-sm">
                  <input
                    type="checkbox"
                    name="products"
                    value={item}
                    checked={formData.products.includes(item)}
                    onChange={handleChange}
                    className="accent-orange-500"
                  />
                  <span>{item}</span>
                </label>
              ))}
            </div>
          </div>
          <div>
            <label htmlFor="details" className="block font-semibold mb-1 text-sm text-gray-700">Message / Details</label>
            <textarea
              name="details"
              value={formData.details}
              onChange={handleChange}
              rows="5"
              className="w-full border border-gray-300 p-3 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-400"
              placeholder="Tell us about what product, shipping port, packaging or other needs..."
            ></textarea>
          </div>
          <button
            type="submit"
            className="w-full bg-orange-500 hover:bg-orange-600 text-white font-bold py-3 rounded-md shadow-lg transition duration-300"
          >
            Submit Quote Request
          </button>
        </form>
      </div>
    </div>
  );
}
