// Home.js (Unified Page with Full Products & Quote)
import React, { useEffect, useRef, useState } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

export default function Home() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);
  const productsRef = useRef(null);
  const quoteRef = useRef(null);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    company: "",
    products: [],
    details: "",
  });

  const scrollToSection = (ref) => {
    ref.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (name === "products") {
      const updatedProducts = [...formData.products];
      if (checked) updatedProducts.push(value);
      else updatedProducts.splice(updatedProducts.indexOf(value), 1);
      setFormData({ ...formData, products: updatedProducts });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/api/send-quote/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      if (data.success) {
        alert("âœ… Your quote request has been sent successfully!");
        setFormData({ name: "", email: "", company: "", products: [], details: "" });
      } else {
        alert("âŒ There was an error: " + data.error);
      }
    } catch (error) {
      alert("âŒ Network error: " + error.message);
    }
  };

  const productOptions = [
    "Arabica Coffee Beans", "Cacao Beans", "Pima Cotton", "Camu Camu Powder",
    "Lucuma Powder", "Baby Alpaca Wool - Royal", "Baby Alpaca Wool - Baby",
    "Baby Alpaca Wool - Regular", "VicuÃ±a Wool", "Mixed Shipment", "Other / Custom"
  ];

  useEffect(() => {
    const scrollRow = (ref) => {
      if (!ref.current) return;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;
      const slide = setInterval(() => {
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;
        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);
      return () => clearInterval(slide);
    };
    const stop1 = scrollRow(row1Ref);
    const stop2 = scrollRow(row2Ref);
    return () => {
      stop1 && stop1();
      stop2 && stop2();
    };
  }, []);

  return (
    <div className="font-sans text-gray-800">
      {/* KEEP ALL EXISTING HOMEPAGE CODE ABOVE */}

      {/* Insert full products section from Products.js here */}
      <div ref={productsRef} className="bg-white pt-32">
        <div className="pt-20 px-8 text-center">
          <h1 className="text-4xl font-bold text-[#002b5c] mb-12 tracking-wide">Our Products</h1>
        </div>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-12 px-10 mb-20">
          {["Peruvian Arabica Coffee", "Cacao Beans", "Pima Cotton", "Camu Camu & Lucuma Powders"].map((title, index) => {
            const descriptions = [
              "Sourced from high-altitude farms in Peru, our 87% purity Arabica coffee is handpicked and sun-dried for peak flavor. Each batch reflects generations of Peruvian expertise and a deep respect for the land.",
              "Rich in cocoa butter and low in acidity, our cacao beans come from fertile valleys in Peru. Ideal for premium chocolate production, our supply ensures traceability, quality, and ethical practices.",
              "Known for its long fibers and unparalleled softness, Peruvian Pima cotton is a luxury textile used by the worldâ€™s top fashion houses. Grown in coastal valleys and harvested with care.",
              "Camu Camu is one of the richest sources of vitamin C on Earth. Lucuma, known as the 'Gold of the Incas', brings natural sweetness to smoothies and desserts. Both grown deep in the Amazon and Andes."
            ];
            return (
              <div key={index} className="bg-white border-2 border-orange-400 rounded-xl p-6 shadow-lg transition transform duration-300">
                <h2 className="text-xl font-bold text-[#002b5c] mb-2">{title}</h2>
                <p className="text-sm text-[#002b5c] leading-relaxed mb-4">{descriptions[index]}</p>
                <a href="#quote" className="inline-block mt-2 text-sm text-orange-500 hover:underline font-semibold">Request This Product â†’</a>
                <p className="text-xs text-gray-500 italic mt-1">Minimum Order: 500kg â€¢ FOB Available</p>
              </div>
            );
          })}
        </div>

        <div className="bg-[#fff8f0] px-12 py-32 text-center">
          <h2 className="text-5xl font-extrabold text-[#002b5c] mb-6 tracking-wide">Baby Alpaca Wool</h2>
          <p className="text-lg max-w-5xl mx-auto text-[#333] leading-loose font-medium">
            Discover the legacy of softness with Baby Alpaca wool â€” warmer than sheep wool, softer than cashmere, and completely hypoallergenic.
            <br /><br /><span className="text-orange-500 font-semibold">Royal Alpaca</span>: The top 1% of alpaca fleece.
            <br /><span className="text-orange-500 font-semibold">Baby Alpaca</span>: Premium texture and warmth.
            <br /><span className="text-orange-500 font-semibold">Regular Alpaca</span>: Versatile and strong.
            <br /><br />Ethically sourced from trusted Andean herders.
          </p>
          <a href="#quote" className="inline-block mt-6 text-sm text-orange-500 hover:underline font-semibold">Request Baby Alpaca Wool â†’</a>
        </div>

        <div className="bg-[#f0f7ff] px-12 py-32 text-center">
          <h2 className="text-5xl font-extrabold text-[#002b5c] mb-6 tracking-wide">VicuÃ±a Wool</h2>
          <p className="text-lg max-w-5xl mx-auto text-[#333] leading-loose font-medium">
            Once reserved for Incan royalty, VicuÃ±a wool is the rarest, finest fiber on Earth. Lightweight, breathable, and warmer than any other natural fiber.
            <br /><br />Sheared only every three years under strict rules.
            <br />Jendyco offers qualified sourcing upon request.
            <br /><br /><span className="text-orange-500 font-semibold">Luxury beyond compare â€” sourced ethically, delivered globally.</span>
          </p>
          <a href="#quote" className="inline-block mt-6 text-sm text-orange-500 hover:underline font-semibold">Request VicuÃ±a Wool â†’</a>
        </div>

        <div className="bg-gray-100 px-8 py-20 overflow-hidden">
          <h2 className="text-2xl font-bold text-center mb-12 text-[#002b5c]">Our Coffee & Cacao in Action â€” Jendy in Motion</h2>
          <div className="overflow-hidden" ref={row1Ref}>
            <div className="flex gap-6 w-max">
              {["JendycoFields1.jpg", "Jendycoffee-Branch2.JPG", "Jendycoffee-branch.JPG", "Jendycocoffeebeans-closeup.JPG"].map((filename, index) => (
                <img key={`row1-${index}`} src={`/images/${filename}`} alt={`Jendyco ${index + 1}`} className="w-[300px] h-[200px] object-cover rounded shadow-md" />
              ))}
            </div>
          </div>
          <div className="overflow-hidden mt-10" ref={row2Ref}>
            <div className="flex gap-6 w-max">
              {["JendycoCoffee-Branch3.JPG", "JendycoCacao-Branch3.JPG", "JendycoCacao-Branch2.JPG", "JendycoCacao-Branch.JPG"].map((filename, index) => (
                <img key={`row2-${index}`} src={`/images/${filename}`} alt={`Jendyco ${index + 5}`} className="w-[300px] h-[200px] object-cover rounded shadow-md" />
              ))}
            </div>
          </div>
        </div>

        <div className="bg-[#002b5c] text-white text-center py-16 px-6">
          <h2 className="text-3xl font-bold mb-4">Global Sourcing, Peruvian Expertise</h2>
          <p className="max-w-3xl mx-auto mb-6 text-lg">
            From superfoods to heritage crafts, if it's from Peru â€” we can find it.
          </p>
          <a href="#quote" className="inline-block bg-orange-500 hover:bg-orange-600 text-white font-semibold px-6 py-3 rounded-full shadow-md transition">
            ðŸ”Ž Request a Custom Product
          </a>
        </div>
      </div>

      {/* QUOTE section stays as-is below */}
    </div>
  );
}
