// HomeMerged.js
import React, { useEffect, useRef, useState } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

export default function HomeMerged() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);
  const quoteRef = useRef(null);
  const productsRef = useRef(null);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    company: "",
    products: [],
    details: "",
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (name === "products") {
      const updatedProducts = [...formData.products];
      if (checked) updatedProducts.push(value);
      else updatedProducts.splice(updatedProducts.indexOf(value), 1);
      setFormData({ ...formData, products: updatedProducts });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/api/send-quote/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (data.success) {
        alert("✅ Your quote request has been sent successfully!");
        setFormData({ name: "", email: "", company: "", products: [], details: "" });
      } else {
        alert("❌ There was an error: " + data.error);
      }
    } catch (error) {
      alert("❌ Network error: " + error.message);
    }
  };

  const scrollToSection = (ref) => {
    ref.current?.scrollIntoView({ behavior: "smooth" });
  };

  const productOptions = [
    "Arabica Coffee Beans", "Cacao Beans", "Pima Cotton", "Camu Camu Powder",
    "Lucuma Powder", "Baby Alpaca Wool - Royal", "Baby Alpaca Wool - Baby",
    "Baby Alpaca Wool - Regular", "Vicuña Wool", "Mixed Shipment", "Other / Custom"
  ];

  useEffect(() => {
    const scrollRow = (ref) => {
      if (!ref.current) return;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;
      const slide = setInterval(() => {
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;
        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);
      return () => clearInterval(slide);
    };
    const stop1 = scrollRow(row1Ref);
    const stop2 = scrollRow(row2Ref);
    return () => {
      stop1 && stop1();
      stop2 && stop2();
    };
  }, []);

  return (
    <div className="flex flex-col min-h-screen font-sans">
      {/* NAVBAR */}
      <div className="w-full bg-gray-900 text-white flex items-center justify-between px-10 md:px-40 py-4 fixed top-0 z-50 shadow-md">
        <div className="w-1/4">
          <img src="/images/jendyco-logo2.png" alt="Logo" className="w-36 md:w-52 h-auto drop-shadow-[0_0_8px_white]" />
        </div>
        <div className="flex-1 flex justify-center space-x-6 md:space-x-10 font-bold text-sm md:text-base uppercase tracking-wide">
          <a href="#who-we-are" className="hover:text-orange-400 transition">Who We Are</a>
          <button onClick={() => scrollToSection(productsRef)} className="hover:text-orange-400 transition">Our Products</button>
          <a href="#contact" className="hover:text-orange-400 transition">Contact Us</a>
          <button onClick={() => scrollToSection(quoteRef)} className="hover:text-orange-400 transition">Request a Quote</button>
        </div>
        <div className="w-1/4" />
      </div>

      {/* HERO + OVERVIEW (existing Home) */}
      {/* ...reuse your existing content as-is above */}

      {/* PRODUCTS */}
      <div ref={productsRef} className="pt-32 px-10 bg-white">
        <h1 className="text-4xl font-bold text-[#002b5c] mb-12 tracking-wide text-center">Our Products</h1>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-12">
          {["Peruvian Arabica Coffee", "Cacao Beans", "Pima Cotton", "Camu Camu & Lucuma Powders"].map((title, index) => {
            const descriptions = [
              "Sourced from high-altitude farms in Peru...",
              "Rich in cocoa butter and low in acidity...",
              "Known for its long fibers and unparalleled softness...",
              "Camu Camu is one of the richest sources of vitamin C..."
            ];
            return (
              <div key={index} className="bg-white border-2 border-orange-400 rounded-xl p-6 shadow-lg">
                <h2 className="text-xl font-bold text-[#002b5c] mb-2">{title}</h2>
                <p className="text-sm text-[#002b5c] mb-4">{descriptions[index]}</p>
                <button onClick={() => scrollToSection(quoteRef)} className="text-sm text-orange-500 hover:underline font-semibold">
                  Request This Product →
                </button>
                <p className="text-xs text-gray-500 italic mt-1">Minimum Order: 500kg • FOB Available</p>
              </div>
            );
          })}
        </div>
      </div>

      {/* Add Baby Alpaca, Vicuña, Slideshow, and Custom CTA here from products.js */}

      {/* QUOTE FORM */}
      <div ref={quoteRef} className="pt-32 px-6 md:px-40 py-16 bg-white">
        <h1 className="text-4xl font-bold text-center text-[#002b5c] mb-10">Request a Quote</h1>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-6 text-sm">
          <div className="flex flex-col"><label>Full Name</label><input type="text" name="name" value={formData.name} onChange={handleChange} className="p-3 rounded border" required /></div>
          <div className="flex flex-col"><label>Email</label><input type="email" name="email" value={formData.email} onChange={handleChange} className="p-3 rounded border" required /></div>
          <div className="flex flex-col md:col-span-2"><label>Company</label><input type="text" name="company" value={formData.company} onChange={handleChange} className="p-3 rounded border" /></div>
          <div className="flex flex-col md:col-span-2">
            <label>Products of Interest</label>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-2 bg-white p-3 border rounded max-h-64 overflow-y-auto">
              {productOptions.map((product) => (
                <label key={product} className="flex items-center space-x-2">
                  <input type="checkbox" name="products" value={product} checked={formData.products.includes(product)} onChange={handleChange} className="accent-orange-500" />
                  <span>{product}</span>
                </label>
              ))}
            </div>
          </div>
          <div className="flex flex-col md:col-span-2">
            <label>Additional Details</label>
            <textarea name="details" value={formData.details} onChange={handleChange} rows="5" className="p-3 rounded border" placeholder="Volume, timeline, delivery port..."></textarea>
          </div>
          <div className="md:col-span-2 text-center mt-6">
            <button type="submit" className="bg-[#ff6f00] hover:bg-orange-600 text-white font-bold px-8 py-3 rounded">Submit Request</button>
          </div>
        </form>
      </div>

      {/* FOOTER */}
      <footer className="bg-gray-200 text-center py-8 text-sm text-gray-600">
        <div className="mb-4 flex justify-center gap-6">
          <a href="https://www.instagram.com/jendyco_international" target="_blank" rel="noopener noreferrer">
            <FaInstagram className="text-[#E1306C] text-2xl hover:scale-110 transition" />
          </a>
          <a href="https://www.linkedin.com/in/jendyco-international-27647b36b" target="_blank" rel="noopener noreferrer">
            <FaLinkedin className="text-[#0077B5] text-2xl hover:scale-110 transition" />
          </a>
        </div>
        © {new Date().getFullYear()} Jendyco International. All rights reserved.
      </footer>
    </div>
  );
}
