import React, { useEffect, useRef } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

const imageBasePath = "/static/frontend/images/";

export default function Products() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);

  useEffect(() => {
    const scrollRow = (ref) => {
      if (!ref.current) return;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;

      const slide = setInterval(() => {
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;

        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);

      return () => clearInterval(slide);
    };

    const stop1 = scrollRow(row1Ref);
    const stop2 = scrollRow(row2Ref);

    return () => {
      stop1 && stop1();
      stop2 && stop2();
    };
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <div className="w-full bg-gray-900 text-white flex items-center justify-between px-10 md:px-40 py-4 fixed top-0 z-50 shadow-md">
        <div className="text-sm font-bold uppercase">
          <a href="/" className="hover:underline text-orange-400">Home</a>
        </div>
        <div className="flex-1 flex justify-center space-x-10 font-bold text-base uppercase">
          <a href="/products" className="hover:text-orange-400">Our Products</a>
          <a href="/#contact" className="hover:underline">Contact Us</a>
          <a href="/quote" className="hover:underline text-orange-400">Request a Quote</a>
        </div>
        <div className="w-1/4 flex justify-end">
          <img
            src={`${imageBasePath}jendyco-logo2.png`}
            alt="Jendyco Logo"
            className="w-40 h-auto drop-shadow-[0_0_8px_white]"
          />
        </div>
      </div>

      <div className="pt-52 px-8 text-center">
        <h1 className="text-5xl font-bold text-[#002b5c] mb-12 tracking-wide">Our Products</h1>
        <p className="max-w-3xl mx-auto text-lg text-gray-700 leading-relaxed">
          We proudly export Peru's finest goods — from premium Arabica coffee and rich cacao to nutrient-packed powders and world-renowned cotton. Whether you want a bulk order or a rare product, we’ll get it done.
        </p>
      </div>

      {/* Expanded Product Sections */}
      <div className="grid md:grid-cols-2 gap-12 px-10 py-16">
        <div className="bg-white p-8 rounded-xl shadow-lg border-l-4 border-orange-500">
          <h2 className="text-2xl font-bold text-[#002b5c] mb-4">Peruvian Coffee</h2>
          <p className="text-gray-700 leading-relaxed">
            Grown at high altitudes and hand-picked by skilled farmers, our 87% purity Arabica beans offer smooth, bold flavors. We work with farms that prioritize sustainable and ethical practices.
          </p>
        </div>

        <div className="bg-white p-8 rounded-xl shadow-lg border-l-4 border-orange-500">
          <h2 className="text-2xl font-bold text-[#002b5c] mb-4">Cacao Beans</h2>
          <p className="text-gray-700 leading-relaxed">
            Our cacao is harvested from Peru’s lush valleys and is known for its rich butter content and deep chocolatey aroma — perfect for premium chocolate production.
          </p>
        </div>

        <div className="bg-white p-8 rounded-xl shadow-lg border-l-4 border-orange-500">
          <h2 className="text-2xl font-bold text-[#002b5c] mb-4">Pima Cotton</h2>
          <p className="text-gray-700 leading-relaxed">
            One of Peru’s proudest exports, Pima cotton is prized for its softness and strength. Ideal for luxury textiles, fashion, and soft-touch goods.
          </p>
        </div>

        <div className="bg-white p-8 rounded-xl shadow-lg border-l-4 border-orange-500">
          <h2 className="text-2xl font-bold text-[#002b5c] mb-4">Camu Camu & Lucuma Powder</h2>
          <p className="text-gray-700 leading-relaxed">
            Sourced from the Amazon and highlands, these superfoods are rich in vitamin C and antioxidants. A global favorite for natural health brands.
          </p>
        </div>
      </div>

      {/* Baby Alpaca Section */}
      <section className="bg-orange-50 px-10 py-20 text-gray-800 text-center">
        <h2 className="text-4xl font-bold mb-6 text-[#002b5c]">Baby Alpaca Wool</h2>
        <p className="max-w-3xl mx-auto text-lg leading-relaxed">
          We offer certified 100% Baby Alpaca wool in three categories — Royal (finest), Baby (softest), and Mixed. Perfect for upscale garments, global textile brands, and boutiques seeking luxurious ethical fibers.
        </p>
      </section>

      {/* Vicuña Wool Section */}
      <section className="bg-white px-10 py-20 text-gray-800 text-center">
        <h2 className="text-4xl font-bold mb-6 text-[#002b5c]">Vicuña Wool (On Demand)</h2>
        <p className="max-w-3xl mx-auto text-lg leading-relaxed">
          We don’t stock Vicuña wool regularly — but thanks to our sourcing network, we can locate it for your project. Let us know what you need and we’ll handle the rest.
        </p>
      </section>

      {/* Custom Sourcing Section */}
      <section className="bg-[#002b5c] px-10 py-20 text-white text-center">
        <h2 className="text-4xl font-bold mb-6 text-orange-400">Need Something Rare?</h2>
        <p className="max-w-3xl mx-auto text-lg leading-relaxed">
          We’re more than exporters — we’re sourcers. Need pink salt, botanicals, exotic herbs, bulk superfoods, or anything else from Peru or Latin America? Jendyco finds it.
        </p>
        <a
          href="/quote"
          className="mt-8 inline-block bg-orange-500 hover:bg-orange-600 text-white font-bold text-lg px-6 py-3 rounded-lg shadow-lg transition"
        >
          Request a Custom Product
        </a>
      </section>

      {/* Image Slideshow */}
      <div className="bg-gray-100 px-8 py-20 overflow-hidden">
        <h2 className="text-2xl font-bold text-center mb-12 text-[#002b5c]">
          Our Products in Action — Jendyco in Motion
        </h2>
        <div className="overflow-hidden" ref={row1Ref}>
          <div className="flex gap-6 w-max">
            {["JendycoFields1.jpg", "Jendycoffee-Branch2.JPG", "Jendycoffee-branch.JPG", "Jendycocoffeebeans-closeup.JPG"].concat(["JendycoFields1.jpg", "Jendycoffee-Branch2.JPG", "Jendycoffee-branch.JPG", "Jendycocoffeebeans-closeup.JPG"]).map((filename, index) => (
              <img
                key={`row1-${index}`}
                src={`${imageBasePath}${filename}`}
                alt={`Jendyco ${index + 1}`}
                className="w-[300px] h-[200px] object-cover rounded shadow-md"
              />
            ))}
          </div>
        </div>

        <div className="overflow-hidden mt-10" ref={row2Ref}>
          <div className="flex gap-6 w-max">
            {["JendycoCoffee-Branch3.JPG", "JendycoCacao-Branch3.JPG", "JendycoCacao-Branch2.JPG", "JendycoCacao-Branch.JPG"].concat(["JendycoCoffee-Branch3.JPG", "JendycoCacao-Branch3.JPG", "JendycoCacao-Branch2.JPG", "JendycoCacao-Branch.JPG"]).map((filename, index) => (
              <img
                key={`row2-${index}`}
