import React, { useEffect, useRef } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

const imageBasePath = "/static/frontend/images/";

export default function Products() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);

  useEffect(() => {
    const scrollRow = (ref) => {
      if (!ref.current) return;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;

      const slide = setInterval(() => {
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;

        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);

      return () => clearInterval(slide);
    };

    const stop1 = scrollRow(row1Ref);
    const stop2 = scrollRow(row2Ref);

    return () => {
      stop1 && stop1();
      stop2 && stop2();
    };
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      {/* Navbar */}
      <div className="w-full bg-gray-900 text-white flex items-center justify-between px-10 md:px-40 py-4 fixed top-0 z-50 shadow-md">
        <div className="text-sm font-bold uppercase">
          <a href="/" className="hover:underline text-orange-400">Home</a>
        </div>
        <div className="flex-1 flex justify-center space-x-10 font-bold text-base uppercase">
          <a href="/products" className="hover:text-orange-400">Our Products</a>
          <a href="/#contact" className="hover:underline">Contact Us</a>
          <a href="/quote" className="hover:underline text-orange-400">Request a Quote</a>
        </div>
        <div className="w-1/4 flex justify-end">
          <img src={`${imageBasePath}jendyco-logo2.png`} alt="Jendyco Logo" className="w-40 h-auto drop-shadow-[0_0_8px_white]" />
        </div>
      </div>

      <div className="pt-52 px-8 text-center">
        <h1 className="text-4xl font-bold text-[#002b5c] mb-12 tracking-wide">Our Products</h1>
      </div>

      {/* Core Products */}
      <section className="px-10 pb-20">
        <h2 className="text-2xl font-bold mb-6 text-[#002b5c]">Flagship Agricultural Exports</h2>
        <div className="grid md:grid-cols-3 gap-12">
          {[
            { title: "Coffee Beans", desc: "Smooth, high-altitude Arabica.", details: "Handpicked 87% Arabica beans with rich, smooth flavors. Ethically grown and export-grade." },
            { title: "Cacao Beans", desc: "Deep chocolate tones. Ethical harvest.", details: "Grown in Peruâ€™s fertile valleys with high butter content and unmatched flavor." },
            { title: "Pima Cotton", desc: "Luxuriously soft. Sustainably grown.", details: "Peruâ€™s textile gem, prized for softness, sheen, and strength." },
          ].map(({ title, desc, details }, idx) => (
            <div key={idx} className="group bg-[#002b5c] text-white p-8 min-h-[320px] rounded-lg shadow-lg transition-all duration-300 hover:scale-105 relative overflow-hidden">
              <h2 className="text-xl font-bold mb-2">{title}</h2>
              <p className="text-sm group-hover:hidden">{desc}</p>
              <div className="hidden group-hover:block absolute inset-0 bg-[#002b5c] bg-opacity-95 p-6">
                <h3 className="text-xl font-bold mb-2">{title}</h3>
                <p className="text-sm">{details}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Premium Natural Textiles */}
      <section className="px-10 pb-20">
        <h2 className="text-2xl font-bold mb-6 text-[#002b5c]">Premium Natural Textiles</h2>
        <div className="grid md:grid-cols-2 gap-10">
          <div className="bg-orange-100 p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold mb-2 text-orange-600">Baby Alpaca Wool</h3>
            <p className="text-gray-800">Known for its luxurious softness and insulation, baby alpaca wool is a premium fiber exported directly from Andean farms.</p>
            <img src={`${imageBasePath}BabyAlpaca.JPG`} alt="Baby Alpaca" className="mt-4 rounded shadow" />
          </div>
          <div className="bg-gray-200 p-6 rounded-lg shadow-inner">
            <h3 className="text-xl font-bold mb-2 text-gray-600">VicuÃ±a Wool <span className="text-sm font-normal">(On Demand)</span></h3>
            <p className="text-gray-700">The rarest natural fiber in the world â€” sourced upon request only. Contact us for exclusive sourcing and export options.</p>
            <img src={`${imageBasePath}VicunaWool.jpg`} alt="VicuÃ±a Wool" className="mt-4 rounded shadow" />
          </div>
        </div>
      </section>

      {/* Functional Superfoods */}
      <section className="px-10 pb-20">
        <h2 className="text-2xl font-bold mb-6 text-[#002b5c]">Peruvian Superfoods</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {[
            { name: "Camu Camu", desc: "Extremely high vitamin C, harvested from the Amazon basin.", img: "CamuCamu.jpg" },
            { name: "Lucuma", desc: "Naturally sweet, low-glycemic and nutritious superfruit.", img: "Lucuma.jpg" },
