import React, { useEffect, useRef } from "react";
import { FaInstagram, FaLinkedin } from "react-icons/fa";

const imageBasePath = "/static/frontend/images/";

export default function Products() {
  const row1Ref = useRef(null);
  const row2Ref = useRef(null);

  useEffect(() => {
    const scrollRow = (ref) => {
      if (!ref.current) return;
      const container = ref.current;
      let scrollAmount = 0;
      const scrollStep = 1;

      const slide = setInterval(() => {
        container.scrollLeft += scrollStep;
        scrollAmount += scrollStep;

        if (scrollAmount >= container.scrollWidth - container.clientWidth) {
          container.scrollLeft = 0;
          scrollAmount = 0;
        }
      }, 40);

      return () => clearInterval(slide);
    };

    const stop1 = scrollRow(row1Ref);
    const stop2 = scrollRow(row2Ref);

    return () => {
      stop1 && stop1();
      stop2 && stop2();
    };
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <div className="w-full bg-gray-900 text-white flex items-center justify-between px-40 py-4 fixed top-0 z-50 shadow-md">
        <div className="text-sm font-bold uppercase">
          <a href="/" className="hover:underline text-orange-400">Home</a>
        </div>
        <div className="flex-1 flex justify-center space-x-10 font-bold text-base uppercase">
          <a href="/products" className="hover:text-orange-400">Our Products</a>
          <a href="/#contact" className="hover:underline">Contact Us</a>
          <a href="/quote" className="hover:underline text-orange-400">Request a Quote</a>
        </div>
        <div className="w-1/4 flex justify-end">
          <img
            src={`${imageBasePath}jendyco-logo2.png`}
            alt="Jendyco Logo"
            className="w-40 h-auto drop-shadow-[0_0_8px_white]"
          />
        </div>
      </div>

      <div className="pt-52 px-8 text-center">
        <h1 className="text-4xl font-bold text-[#002b5c] mb-12 tracking-wide">Our Products</h1>
      </div>

      {/* Natural Goods Section */}
      <div className="grid md:grid-cols-3 gap-12 px-10 mb-5">
        <ProductCard color="#002b5c" title="Coffee Beans" descShort="Smooth, aromatic, high-altitude Arabica." descLong="Handpicked from high-altitude Peruvian farms, our 87% Organic Arabica beans offer rich, smooth flavors with ethical sourcing and export-grade consistency." />
        <ProductCard color="#ff6f00" title="Cacao Beans" descShort="Deep chocolate tones. Ethical harvest." descLong="Sourced from Peruâ€™s fertile valleys, our cacao is known for its high butter content, low acidity, and unmatched flavor profile." />
        <ProductCard color="#002b5c" title="Pima Cotton" descShort="Luxuriously soft. Sustainably grown." descLong="Peruâ€™s signature textile, Pima cotton is prized for its extraordinary softness, strength, and sheen." />
      </div>

      {/* Luxury & Special Sourcing Section */}
      <div className="grid md:grid-cols-2 gap-12 px-10 mt-10">
        <ProductCard color="#8e6e53" title="Baby Alpaca" descShort="Premium fiber. Softest touch." descLong="Sourced directly from the Peruvian Andes, our baby alpaca wool is prized for its warmth, silky texture, and high-end appeal in global markets." />
        <ProductCard color="#444444" title="VicuÃ±a Wool (On-Demand)" descShort="Rare. Regal. Reserved." descLong="One of the rarest and most luxurious fibers on Earth. Available only through custom sourcing partnerships. Serious inquiries only." />
      </div>

      {/* Sourcing Flex Section */}
      <div className="text-center px-10 mt-20">
        <h2 className="text-3xl font-bold text-[#002b5c] mb-6">Looking for something else?</h2>
        <p className="text-lg max-w-3xl mx-auto">
          Whether itâ€™s Camu Camu powder, Lucuma, Maca root, or any natural product grown in Peru â€” we can source it. Our sourcing network also extends globally through trusted partners.
        </p>
        <a href="/quote" className="mt-6 inline-block bg-orange-500 text-white px-6 py-3 rounded-lg shadow-lg hover:bg-orange-600 transition">
          Request Custom Sourcing
        </a>
      </div>

      {/* Slideshow Section */}
      <div className="bg-gray-100 px-8 py-20 overflow-hidden mt-20">
        <h2 className="text-2xl font-bold text-center mb-12 text-[#002b5c]">
          Our Coffee & Cacao in Action â€” Jendy in Motion
        </h2>

        <div className="overflow-hidden" ref={row1Ref}>
          <div className="flex gap-6 w-max">
            {["JendycoFields1.jpg", "Jendycoffee-Branch2.JPG", "Jendycoffee-branch.JPG", "Jendycocoffeebeans-closeup.JPG"].map((filename, index) => (
              <img
                key={`row1-${index}`}
                src={`${imageBasePath}${filename}`}
                alt={`Jendyco ${index + 1}`}
                className="w-[300px] h-[200px] object-cover rounded shadow-md"
              />
            ))}
          </div>
        </div>

        <div className="overflow-hidden mt-10" ref={row2Ref}>
          <div className="flex gap-6 w-max">
            {["JendycoCoffee-Branch3.JPG", "JendycoCacao-Branch3.JPG", "JendycoCacao-Branch2.JPG", "JendycoCacao-Branch.JPG"].map((filename, index) => (
              <img
                key={`row2-${index}`}
                src={`${imageBasePath}${filename}`}
                alt={`Jendyco ${index + 5}`}
                className="w-[300px] h-[200px] object-cover rounded shadow-md"
              />
            ))}
          </div>
        </div>
      </div>

      <a
        href="/quote"
        className="fixed bottom-6 right-6 bg-orange-500 text-white px-4 py-3 rounded-full shadow-lg hover:bg-orange-600 z-50 transition"
      >
        ðŸ“¦ Request a Quote
      </a>

      <footer className="bg-gray-200 text-center py-8 text-sm text-gray-600">
        <div className="mb-4 flex justify-center gap-6">
          <a
            href="https://www.instagram.com/jendyco_international"
            target="_blank"
            rel="noopener noreferrer"
            aria-label="Instagram"
          >
            <FaInstagram className="text-[#E1306C] text-2xl hover:scale-110 transition" />
          </a>
          <a
            href="https://www.linkedin.com/in/jendyco-international-27647b36b"
            target="_blank"
            rel="noopener noreferrer"
            aria-label="LinkedIn"
          >
            <FaLinkedin className="text-[#0077B5] text-2xl hover:scale-110 transition" />
          </a>
        </div>
        Â© {new Date().getFullYear()} Jendyco International. All rights reserved.
      </footer>
    </div>
  );
}

function ProductCard({ color, title, descShort, descLong }) {
  return (
    <div className={`group bg-[${color}] text-white p-8 min-h-[320px] rounded-lg shadow-lg transition-all duration-300 hover:scale-105 hover:z-10 relative overflow-hidden`}>
      <h2 className="text-xl font-bold mb-2">{title}</h2>
      <p className="text-sm leading-relaxed group-hover:hidden">{descShort}</p>
      <div className="hidden group-hover:block absolute inset-0 bg-opacity-95 p-6" style={{ backgroundColor: color }}>
        <h3 className="text-xl font-bold mb-2">{title}</h3>
        <p className="text-sm leading-relaxed">{descLong}</p>
      </div>
    </div>
  );
}
